alfredsnippet.snippet:
// Add item to a map
a := map[string]int{}
a["car"] = 3
a["truck"] = 1

// Remove item from a map
delete(a, "car")

// Access items with a subscript
b := a["truck"] // 1

// Add item to a slice
c := []int{3, 4, 2, 1, 8}
c = append(c, 2)

// Remove item at index 1 from the slice
c = append(c[:1], c[2:]...)

// Access items with a subscript
d := c[2] // 1
